from django.contrib.auth.models import Userfrom django.db import modelsfrom django.core.validators import MaxValueValidator, MinValueValidatorclass Room(models.Model):    name = models.CharField(max_length=36)  # name of the room    description = models.TextField(max_length=360)class DataType(models.Model):    room = models.ForeignKey(Room, on_delete=models.CASCADE)    user = models.ForeignKey(User, on_delete=models.CASCADE)    TYPES_OF_DATA = (        ('Humidity'),        ('Tempurature'),        ('Co2 Level'),    )    type = models.CharField(max_length=1, choices=TYPES_OF_DATA)class TimePeriod(models.Model):    user = models.ForeignKey(User, on_delete=models.CASCADE)    room = models.ForeignKey(Room, on_delete=models.CASCADE)    TIME_CHOICE = (        ('1hr', 'The Last Hour'),        ('1d', '1 day Ago'),        ('3d', '3 days Ago '),        ('1w', '1 week ago'),    )    time = models.CharField(max_length=1, choices=TIME_CHOICE)